const entrada = $input.all()[0].json;

// Pega o conte√∫do da OpenAI
let respostaCompleta = entrada.message?.content || entrada.content || "";

// Extrai mensagem e dados
let msg = "";
let dadosExtraidos = {};

if (respostaCompleta.includes("MENSAGEM:") && respostaCompleta.includes("DADOS_EXTRAIDOS:")) {
  const inicioMsg = respostaCompleta.indexOf("MENSAGEM:") + 9;
  const fimMsg = respostaCompleta.indexOf("DADOS_EXTRAIDOS:");
  msg = respostaCompleta.substring(inicioMsg, fimMsg).trim();

  const inicioJson = respostaCompleta.indexOf("DADOS_EXTRAIDOS:") + 16;
  const jsonStr = respostaCompleta.substring(inicioJson).trim();

  try {
    dadosExtraidos = JSON.parse(jsonStr);
  } catch (e) {
    console.error('Erro ao fazer parse do JSON:', e);
  }
} else {
  msg = respostaCompleta;
}

return [{
  json: {
    ...entrada,
    ...dadosExtraidos,
    mensagem_mariana_loop: msg,
    telefone_e164: entrada.telefone_e164,
    qualificacao_completa: dadosExtraidos.qualificacao_completa === true
  }
}];

const msg = $json.mensagem_mariana || '';
const len = msg.length;

// calcula delay baseado no tamanho da mensagem
