try {
  let dados = {};

  // Tenta pegar body primeiro, sen√£o pega o json direto
  const raw = $input.all()[0].json;
  if (typeof raw === 'string') {
    dados = JSON.parse(raw);
  } else if (raw.body) {
    dados = typeof raw.body === 'string' ? JSON.parse(raw.body) : raw.body;
  } else {
    dados = raw;
  }

  const limparTelefone = (telefone) => { if (!telefone) return ''; const limpo = String(telefone).replace(/\D/g, '').slice(-11); return /^\d{11}$/.test(limpo) ? limpo : ''; };
  const calcularIdade = (nasc) => { if (!nasc) return ''; let d; if (/^\d{2}\/\d{2}\/\d{4}$/.test(String(nasc))) { const [dd, mm, yyyy] = String(nasc).split('/').map(n=>parseInt(n,10)); d = new Date(yyyy, mm-1, dd); } else { d = new Date(nasc);} if (isNaN(d.getTime())) return ''; const h=new Date(); let idade=h.getFullYear()-d.getFullYear(); const m=h.getMonth()-d.getMonth(); if(m<0||(m===0&&h.getDate()<d.getDate())) idade--; return idade>0?String(idade):''; };
  const validarEmail = (email) => { if (!email) return ''; const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; return re.test(String(email).trim()) ? String(email).trim() : ''; };

  const toBucket = (raw) => { if (raw >= 70) return { classificacao: 'Alta', score: 80 }; if (raw >= 50) return { classificacao: 'M√©dia', score: 60 }; if (raw >= 30) return { classificacao: 'Baixa', score: 40 }; return { classificacao: 'Muito Baixa', score: 20 }; };

  const calcularScore = (d) => { let score = 0; if (d.nome && String(d.nome).trim().length > 2) score += 20; if (d.email && validarEmail(d.email)) score += 20; if ((d.telefone && limparTelefone(d.telefone)) || (d.telefone_limpo && limparTelefone(d.telefone_limpo))) score += 20; if (d.estado && String(d.estado).trim().length > 1) score += 10; if (d.cidade && String(d.cidade).trim().length > 1) score += 10; const totalVidas = parseInt(d.total_vidas ?? d.vidas ?? 0, 10) || 0; if (totalVidas > 0) score += 10; if (d.mensagem && String(d.mensagem).trim().length > 10) score += 10; const idade = parseInt(d.idade_titular ?? d.idade ?? 0, 10) || 0; if (idade >= 60 && idade <= 68) score -= 15; else if (idade >= 69) score -= 35; const preexist = String(d.tem_preexistencia || '').toLowerCase(); if (preexist.startsWith('sim')) score -= 20; const grave = String(d.doenca_grave || '').toLowerCase(); if (grave.startsWith('sim')) score -= 35; if (totalVidas > 0 && totalVidas < 3) score -= 15; const tipo = String(d.tipo_contratacao || '').toLowerCase(); if ((tipo.includes('empres') || tipo.includes('mei')) && totalVidas > 0 && totalVidas < 3) score -= 10; const cnpjMeses = parseInt(d.cnpj_meses_ativo ?? 0, 10) || 0; if ((tipo.includes('empres') || tipo.includes('mei')) && cnpjMeses > 0 && cnpjMeses < 6) score -= 25; const plano = String(d.plano_atual || '').trim(); const tempoPlanoMeses = parseInt(d.tempo_plano_atual_meses ?? 0, 10) || 0; if (!plano || plano.toLowerCase() === 'n√£o possui' || plano.toLowerCase() === 'nao possui') { score += 10; } else if (tempoPlanoMeses >= 24) { score += 15; } const score_bruto = Math.max(0, Math.min(100, score)); const bucket = toBucket(score_bruto); return { score_bruto, score: bucket.score, classificacao: bucket.classificacao }; };

  const telefoneLimpo = limparTelefone(dados.telefone || dados.from);
  const idadeCalculada = calcularIdade(dados.nascimento);
  const emailValidado = validarEmail(dados.email);
  const bucket = calcularScore(dados);

  // Emoji para classifica√ß√£o inicial
  const classificacao_inicial_emoji = bucket.classificacao === 'Alta' ? 'üî• Alta' : (bucket.classificacao === 'M√©dia' ? 'üôÇ M√©dia' : (bucket.classificacao === 'Baixa' ? '‚ùÑÔ∏è Baixa' : '‚ùÑÔ∏è Muito Baixa'));

  return [{
    lead_id: `lead_${Date.now()}_${Math.random().toString(36).slice(2, 11)}`,
    nome: String(dados.nome || '').trim(),
    email: emailValidado,
    telefone: String(dados.telefone || ''),
    telefone_limpo: telefoneLimpo,
    telefone_e164: telefoneLimpo ? `+55${telefoneLimpo}` : '',
    nascimento: String(dados.nascimento || ''),
    estado: String(dados.estado || '').trim(),
    cidade: String(dados.cidade || '').trim(),
    vidas: parseInt(dados.vidas, 10) || 1,
    total_vidas: parseInt(dados.total_vidas ?? dados.vidas ?? 0, 10) || (parseInt(dados.vidas, 10) || 1),
    mensagem: String(dados.mensagem || '').trim(),
    timestamp: new Date().toISOString(),

    idade: idadeCalculada,
    idade_titular: idadeCalculada,

    cnpj_meses_ativo: dados.cnpj_meses_ativo ?? '',
    tempo_plano_atual_meses: dados.tempo_plano_atual_meses ?? '',
    tipo_contratacao: dados.tipo_contratacao ?? (dados.tipo || ''),
    tem_preexistencia: dados.tem_preexistencia ?? '',
    doenca_grave: dados.doenca_grave ?? '',
    plano_atual: dados.plano_atual ?? '',

    status: 'Novo - Aguardando IA',
    classificacao: bucket.classificacao,
    classificacao_inicial: bucket.classificacao,
    classificacao_inicial_emoji,

    score_bruto: bucket.score_bruto,
    score: bucket.score,
    score_inicial: bucket.score,

    remarketing_data: '',
    telefone_valido: Boolean(telefoneLimpo),
    telefone_invalido: !telefoneLimpo,
    dados_validos: Boolean(telefoneLimpo && String(dados.nome || '').trim().length > 2)
  }];

} catch (error) {
  return [{
    lead_id: `lead_${Date.now()}_${Math.random().toString(36).slice(2, 11)}`,
    nome: 'Erro no processamento',
    email: '',
    telefone: '',
    telefone_limpo: '',
    telefone_e164: '',
    nascimento: '',
    estado: '',
    cidade: '',
    vidas: 1,
    mensagem: 'Erro ao processar dados iniciais',
    timestamp: new Date().toISOString(),
    idade: '',
    idade_titular: '',
    status: 'Erro - Dados Inv√°lidos',
    classificacao: 'Muito Baixa',
    classificacao_inicial: 'Muito Baixa',
    classificacao_inicial_emoji: '‚ùÑÔ∏è Muito Baixa',
    score: 0,
    score_inicial: 0,
    score_bruto: 0,
    remarketing_data: '',
    telefone_valido: false,
    telefone_invalido: true,
    dados_validos: false,
    erro_processamento: error.message
  }];
}

const data = $getWorkflowStaticData('global');
data.execucoesAtivas = (data.execucoesAtivas || 0) + 1;
if (data.execucoesAtivas > 30) {
  return [{ fila: true, motivo: 'Muitas execu√ß√µes, tente novamente' }];
