return $input.all();

const data = $json;

// Identifica se √© texto ou √°udio
const isAudio = data.messageType === 'audioMessage' || 
                data.audio || 
                data.audioUrl || 
                data.body?.data?.message?.audioMessage;

const telefone = data.from?.replace(/[^0-9]/g, '') || 
                 data.body?.data?.key?.remoteJid?.replace(/[^0-9]/g, '') || '';

return [{
