  texto_mensagem: data.text || data.message || data.body?.data?.message?.conversation || data.body?.data?.message?.extendedTextMessage?.text || ''
}];

const audioTranscrito = $json.text || $json.transcription || '';
const dadosOriginais = $input.item(0).json;

return [{
  ...dadosOriginais,
  texto_mensagem: audioTranscrito,
  transcricao_completa: true
}];

// Add a new field called 'myNewField' to the JSON of the item
$input.item.json.myNewField = 1;

return $input.item;

const dados = $input.all()[0].json;

// Resposta j√° vem no campo correto
const textoResposta = dados.resposta_cliente || '';

// Extra√ß√£o autom√°tica APENAS se campo ainda vazio
let dadosExtraidos = {...dados};

// IDADE (s√≥ se n√£o tiver)
if (!dadosExtraidos.idade_titular) {
  const idadeMatch = textoResposta.match(/(\d+)\s*anos?/i);
  if (idadeMatch) dadosExtraidos.idade_titular = idadeMatch[1];
}

// VIDAS (s√≥ se n√£o tiver ou for 1)
if (!dadosExtraidos.total_vidas || dadosExtraidos.total_vidas === 1) {
  const vidasMatch = textoResposta.match(/(\d+)\s*(vidas?|pessoas?|dependentes?)/i);
  if (vidasMatch) dadosExtraidos.total_vidas = vidasMatch[1];
}

// TIPO CONTRATA√á√ÉO (s√≥ se n√£o tiver)
if (!dadosExtraidos.tipo_contratacao) {
  if (/empresa|mei|cnpj/i.test(textoResposta)) {
    dadosExtraidos.tipo_contratacao = 'Empresarial';
  } else if (/famili|fam√≠lia/i.test(textoResposta)) {
    dadosExtraidos.tipo_contratacao = 'Familiar';
  } else if (/individual/i.test(textoResposta)) {
    dadosExtraidos.tipo_contratacao = 'Individual';
  }
}

// IDADES DEPENDENTES (s√≥ se n√£o tiver e vidas > 1)
if (!dadosExtraidos.idades_dependentes && parseInt(dadosExtraidos.total_vidas || 0) > 1) {
  const todasIdades = textoResposta.match(/\d{1,2}\s*anos/gi);
  if (todasIdades && todasIdades.length > 1) {
    dadosExtraidos.idades_dependentes = todasIdades.map(i => i.match(/\d+/)[0]).join(', ');
  }
}

// HOSPITAL (s√≥ se n√£o tiver)
if (!dadosExtraidos.hospital_preferencia) {
  if (/einstein/i.test(textoResposta)) dadosExtraidos.hospital_preferencia = 'Einstein';
  else if (/s√≠rio|sirio/i.test(textoResposta)) dadosExtraidos.hospital_preferencia = 'S√≠rio-Liban√™s';
}

// PLANO ATUAL (s√≥ se n√£o tiver)
if (!dadosExtraidos.plano_atual) {
  if (/unimed/i.test(textoResposta)) dadosExtraidos.plano_atual = 'Unimed';
  else if (/sulamerica|sulam√©rica/i.test(textoResposta)) dadosExtraidos.plano_atual = 'SulAm√©rica';
  else if (/n√£o tenho|nao tenho|sem plano/i.test(textoResposta)) dadosExtraidos.plano_atual = 'N√£o possui';
}

// PREEXIST√äNCIA (s√≥ se n√£o tiver)
if (!dadosExtraidos.tem_preexistencia) {
  if (/sim/i.test(textoResposta) && /doen√ßa|preexist/i.test(textoResposta)) {
    dadosExtraidos.tem_preexistencia = 'sim';
  }
}

// DOEN√áA GRAVE (s√≥ se n√£o tiver)
if (!dadosExtraidos.doenca_grave) {
  if (/cancer|c√¢ncer|diabetes|hipertens/i.test(textoResposta)) {
    dadosExtraidos.tem_preexistencia = 'sim';
    if (/cancer|c√¢ncer|transplante|dialise/i.test(textoResposta)) {
      dadosExtraidos.doenca_grave = 'sim';
    }
  }
}

return [{
  json: dadosExtraidos
}];
return [{
  json: {
    ...dadosExtraidos,
    telefone_e164: dadosExtraidos.telefone_e164  // adicione esta linha
  }
}];

const dados = $input.all()[0].json;

// Recalcula score final
const calcularScoreFinal = (d) => {
  let score = 0;

  // Dados b√°sicos
  if (d.nome) score += 20;
  if (d.email) score += 20;
  if (d.telefone_limpo) score += 20;
  if (d.estado) score += 10;
  if (d.cidade) score += 10;

  const totalVidas = parseInt(d.total_vidas || d.vidas || 0, 10);
  if (totalVidas > 0) score += 10;

  // Qualifica√ß√£o completa
  if (d.idade_titular) score += 10;
  if (d.tipo_contratacao) score += 10;
  if (d.hospital_preferencia) score += 5;

  // Penalidades
  const idade = parseInt(d.idade_titular || 0, 10);
  if (idade >= 60 && idade <= 68) score -= 15;
  else if (idade >= 69) score -= 35;

  if (String(d.tem_preexistencia || '').toLowerCase().startsWith('sim')) score -= 20;
  if (String(d.doenca_grave || '').toLowerCase().startsWith('sim')) score -= 35;

  if (totalVidas > 0 && totalVidas < 3) score -= 15;

  const tipo = String(d.tipo_contratacao || '').toLowerCase();
  if ((tipo.includes('empres') || tipo.includes('mei')) && totalVidas < 3) score -= 10;

  const cnpjMeses = parseInt(d.cnpj_meses_ativo || 0, 10);
  if ((tipo.includes('empres') || tipo.includes('mei')) && cnpjMeses < 6) score -= 25;

  const plano = String(d.plano_atual || '').trim();
  const tempoPlanoMeses = parseInt(d.tempo_plano_atual_meses || 0, 10);
  if (!plano || plano.toLowerCase() === 'n√£o possui') {
    score += 10;
  } else if (tempoPlanoMeses >= 24) {
    score += 15;
  }

  score = Math.max(0, Math.min(100, score));

  // Classifica√ß√£o por bucket
  let classificacao;
  if (score >= 70) classificacao = 'Alta';
  else if (score >= 50) classificacao = 'M√©dia';
  else if (score >= 30) classificacao = 'Baixa';
  else classificacao = 'Muito Baixa';

  return { score, classificacao };
};

const scoreFinal = calcularScoreFinal(dados);

// Classifica√ß√£o com emoji
let classificacao_final_emoji = '';
if (scoreFinal.classificacao === 'Alta') classificacao_final_emoji = 'üî• Alta';
else if (scoreFinal.classificacao === 'M√©dia') classificacao_final_emoji = 'üôÇ M√©dia';
else if (scoreFinal.classificacao === 'Baixa') classificacao_final_emoji = '‚ùÑÔ∏è Baixa';
else classificacao_final_emoji = '‚ùÑÔ∏è Muito Baixa';

return [{
  ...dados,
  score_final: scoreFinal.score,
  classificacao_final: scoreFinal.classificacao,
  classificacao_final_emoji: classificacao_final_emoji,
